import '../Styling/Auth.css';
import Logo from '../IMGs/Logos/ChenoChatIcon.svg';
import {Link} from 'react-router-dom';
import {useEffect, useRef} from 'react';
import ReturnLink from './Subcomponent/ReturnLink';

export default function Register(){

    
    // let registerContainer = document.getElementById(`register-auth-form`);
    
    let xCord = null;
    let yCord = null;
    let location = null;

    let C1B = 55
    let C2B = 45;

    let color1 = `rgba(${C1B},${C1B},${C1B}, 0.75)`;
    let color2 = `rgba(${C2B},${C2B},${C2B}, 0.75)`;

    
    let registerContainerRef = useRef(null);

    useEffect(()=>{
        if(registerContainerRef.current){
            console.log()
        }
    }, [])

    function registerFormHovered(event){
        // location = registerContainer.getBoundingClientRect();
        // xCord = event.clientX - location.x;
        // yCord = event.clientY - location.y;
        // registerContainer.style.background = `radial-gradient(circle at ${xCord}px ${yCord}px, ${color1}, ${color2})`;
    }

    function registerFormExited(event){
        // let i = 0;
        // let curr = 0;
        // do{
        //     setTimeout(()=>{
        //         event.target.style.background =
        //             `radial-gradient(circle at ${xCord}px ${yCord}px, ${color2}, ${color1} ${curr++  * 3}%)`;
        //     }, i * 2);
        //     i++;
        // }while(i < 100)
    }

    let children = document.querySelectorAll(`#register-auth-form > *`);
    children.forEach( item =>{
        item.addEventListener(`mousemove`, registerFormHovered)
    })
    

    return (
        <div className='auth-form' id='register-auth-form'  ref={registerContainerRef} onMouseMove={registerFormHovered} onMouseLeave={registerFormExited}>
            <ReturnLink to={'/Login'} />
            <img src={Logo} className='auth-logo'/>
            <div className='auth-title'>ChenoChat</div>
            <div className='auth-description'>
                ChenoChat, a place to connect with friends and family, and share connections today!
            </div>
            <div className='auth-type'>Register</div>
            <form className='register-container'>
                {/* Registration Data */}
                <fieldset>
                    <legend align='left'>Username</legend>
                    <input></input>
                </fieldset>
                <fieldset>
                    <legend align='left'>Password</legend>
                    <input></input>
                </fieldset>
                <fieldset>
                    <legend align='left'>Confirm Password</legend>
                    <input></input>
                </fieldset>
                <div className='security-question-divider'></div>
                {/* Security Question */}
                <fieldset>
                    <legend>Security Question</legend>
                    <select className='auth-select'>
                        <option>Select a question</option>
                        <option>What was your first pet's name?</option>
                        <option>In which city did your parents meet?</option>
                        <option>What city were you born in?</option>
                        <option>What is your grandmother's maidin name?</option>
                        <option>What is the manufacturer of your first car?</option>
                    </select>
                </fieldset>
                <fieldset>
                    <legend >Security Answer</legend>
                    <input></input>
                </fieldset>
                
                {/* Submit */}
                <div className='auth-submit'>
                    <button className='auth-register-button'>Register</button>
                </div>
            </form>
        </div>
    )
}
