import react, {useEffect, useState} from 'react';
import {Link} from 'react-router-dom';
import Logo from '../IMGs/Logos/ChenoChatIcon.svg';

import '../Styling/Splash.css';


export default function SplashBackground(){

    let IDElement = x =>{ return document.getElementById(x)};

    let [useIntro, updateUsedIntro] = useState(true);

    useEffect(()=>{

        console.log(useIntro);

        let logoTime = 2;

        if(useIntro){
            IDElement('id-splash-logo').style.animation = `splash-logo-intro ${logoT}s forwards`
            IDElement('id-splash-title').style.animation = 'splash-title-intro 1s 3s forwards'
        }
        else{
            
        }
    }, [useIntro])

    return (
        <div className='splashscreen-container'>
            <img src={Logo} 
                className='splash-logo'
                id='id-splash-logo'
                style={{opacity: (useIntro) ? 0 : 1}}
            />
            <div className='splash-text'>
                <h1 
                    className='splash-title'
                    id='id-splash-title'
                    style={{opacity: (useIntro) ? 0 : 1}}
                > 
                    ChenoChat 
                </h1>
                <p 
                    className='splash-description'
                    style={{
                        opacity: (useIntro) ? 0 : 1}
                    }
                >
                    Join and connect with friends and family. 
                </p>
            </div>
            <Link to='/Login'
                className='splash-enter' 
                style={{opacity: (useIntro) ? 0 : 1}}
            >
                Enter
            </Link>
        </div>
    )
}
