import react, {useEffect, useState} from 'react';
import {Link} from 'react-router-dom';
import Logo from '../IMGs/Logos/ChenoChatIcon.png';

import '../Styling/Splash.css';

export default function Splashscreen(){

    let [useIntro, updateUsedIntro] = useState(true);

    useEffect(()=>{
        if(useIntro == false){
            console.log('')
        }
    }, [useIntro])

    return (
        <div className='splashscreen-container'>
            <img src={Logo} id='splashLogo'></img>
            <h1> ChenoChat </h1>
            <p>
                Enter ChenoChat, connect with family and friends.
            </p>
            <Link to='/Login' className='splash-enter'>Enter</Link>
        </div>
    )
}
