    }
    let container = document.getElementById(parent);
    let location = container.getBoundingClientRect();
    let xCord = event.clientX - location.x;
    let yCord = event.clientY - location.y;
    container.style.background = `radial-gradient(circle at ${xCord}px ${yCord}px, ${color1}, ${color2} 65%)`;
}   

export function containerExited(event, duration, color1, color2, demo){
    let location = event.target.getBoundingClientRect();
    let xCord = event.clientX - location.x;
    let yCord = event.clientY - location.y;

    let i = 0;  
    let curr = 0;
    do{
        setTimeout(()=>{
            event.target.style.background =
                `radial-gradient(circle at ${xCord}px ${yCord}px, ${color2}, ${color1} ${curr++  * 3}%)`;
        }, i * duration);
        i++;
    }while(i < 100)
}
