import '../Styling/Auth.css';
import Logo from '../IMGs/Logos/ChenoChatIcon.svg';
import ReturnLink from './Subcomponent/ReturnLink';
import {containerExited, containerHovered } from './SharedFunctions/Background';
import ReCaptcha from 'react-google-recaptcha';
import {useRef, useState} from 'react';

export default function Register(){

    let refCaptcha = useRef(null);
    let usernameInvalidRegex = /[^a-zA-Z0-9]+/

    let [registerData, updateRegisterData] = useState({
        username: '',
        password: '',
        confirm: '',
        birthday: '',
        securityQ: '',
        securityA: '',
    })
    function updateRegisterDataFunc(event){
        let {name, value, id} = event.target;
        
        // Update registerData with the updated value
        updateRegisterData(old => {return {...old, [name]: value}})
        
        document.getElementById(id).parentElement.style.borderColor = 'grey'
        document.getElementById(id).parentElement.style.color = 'orange'

        if(id == 'register-password' || id == 'register-confirm'){
            // Incorrect matching indicator UI changes
            document.getElementById(`register-password`).parentElement.style.borderColor = 'grey';
            document.getElementById(`register-confirm`).parentElement.style.borderColor = 'grey';
            document.getElementById(`register-password-label`).style.color = 'orange'
            document.getElementById(`register-confirm-label`).innerText = 'Confirm Password'
            document.getElementById(`register-confirm-label`).style.color = 'orange'
        }
        // Check if username input contains bad data
        if(name == 'username'){
            if(usernameInvalidRegex.test(value)){
                document.getElementById(`register-username`).parentElement.style.borderColor = 'red';
                document.getElementById(`register-username-label`).style.color = 'red';
                document.getElementById(`register-username-label`).innerText = 'Only letters and numbers valid'
            }
            else{
                document.getElementById(`register-username`).parentElement.style.borderColor = 'grey';
                document.getElementById(`register-username-label`).style.color = 'Orange';
                document.getElementById(`register-username-label`).innerText = 'Username'
            }
        }
    }
    function submitRegisterData(event){
        event.preventDefault();
        
        const recaptchaVal = refCaptcha.current.getValue()

        var dataValid = true;

        // Check if a value was left empty
        for(let key in registerData){
            if(registerData[key] == ''){
                dataValid = false;
                document.getElementById(`register-${key}`).parentElement.style.borderColor = 'red'
                document.getElementById(`register-${key}`).parentElement.style.color = 'red'
            }
            else{
                document.getElementById(`register-${key}`).parentElement.style.borderColor = 'grey'
                document.getElementById(`register-${key}`).parentElement.style.color = 'orange'
            }
        }


        // Check if password and confirm passwords are matched
        if(registerData.password != registerData.confirm){
            dataValid = false;
            // Clear password/confirm inputs
            document.getElementById('register-password').value = '';
            document.getElementById('register-confirm').value = '';
            // Set password/confirm AI error indicators
            document.getElementById(`register-password`).parentElement.style.borderColor = 'red';
            document.getElementById(`register-confirm`).parentElement.style.borderColor = 'red';
            document.getElementById(`register-password-label`).style.color = 'red'
            document.getElementById(`register-confirm-label`).innerText = 'Incorrect Match'
            document.getElementById(`register-confirm-label`).style.color = 'red'
        }

        // Check if grecaptcha has not been selected by the user
        if(!recaptchaVal){
            dataValid = false;
            document.querySelector(`#auth-register-recaptcha iframe`).style.borderColor = 'red'
        }
        else{
            document.querySelector(`#auth-register-recaptcha iframe`).style.borderColor = 'grey'
        }

        // If dataValid has never been turned false, data valid, proceed to web server for further validation. 
        if(dataValid){
            
            let request = new XMLHttpRequest();
            request.open('POST', '/authentication/register');
            request.send({capToken: recaptchaVal, data: registerData});
            
        }
        
    }
	
    return (
        <div className='auth-form' 
            id='register-auth-form' 
            onMouseMove={(event)=>{
                containerHovered(event, `register-auth-form`, `rgba(55, 55, 55, 0.75)`, `rgba(45, 45, 45, 0.75)`);
            }} 
            onMouseLeave={(event)=>{
                containerExited(event, 3, 'rgba(55, 55, 55, 0.75)', 'rgba(45, 45, 45, 0.75)')
            }}
        >
            <ReturnLink to={'/Login'} />
            <img src={Logo} className='auth-logo'/>
            <div className='auth-title'>ChenoChat</div>
            <div className='auth-description'>
                ChenoChat, a place to connect with friends and family, and share connections today!
            </div>
            <div className='auth-type'>Register</div>
            <form className='register-container'
                onSubmit={submitRegisterData}
            >
                {/* Registration Data */}
                <fieldset>
                    <legend align='left' id='register-username-label'>Username</legend>
                    <input type='text'
                        name='username'
                        id='register-username'
                        placeholder='create a username'
                        maxLength={35}
                        value={registerData.username}
                        onChange={updateRegisterDataFunc}
                    />
                </fieldset>
                <fieldset>
                    <legend align='left' id='register-password-label'>Password</legend>
                    <input type='password' 
                        name='password'
                        id='register-password'
                        maxLength={35}
                        placeholder='enter password'
                        value={registerData.password}
                        onChange={updateRegisterDataFunc}
                    />
                </fieldset>
                <fieldset>
                    <legend align='left' id='register-confirm-label'>Confirm Password</legend>
                    <input type='password' 
                        name='confirm'
                        id='register-confirm'
                        maxLength={35}
                        placeholder='confirm password'
                        value={registerData.confirmPass}
                        onChange={updateRegisterDataFunc}
                    />
                </fieldset>
                <div className='security-question-divider'></div>
                <fieldset>
                    <legend align='left'>Birthday</legend>
                    {/* <input type='date'></input> */}
                    <input type='date' 
                        name='birthday'
                        id='register-birthday'
                        min="1914-07-28"
                        max="2020-01-01"
                        value={registerData.birthday}
                        onChange={updateRegisterDataFunc}
                    />
                </fieldset>
                <div className='security-question-divider'></div>
                {/* Security Question */}
                <fieldset>
                    <legend>Security Question</legend>
                    <select className='auth-select'
                        value={registerData.securityQ}
                        name='securityQ'
                        id='register-securityQ'
                        onChange={updateRegisterDataFunc}
                    >
                        <option value=''>Select a question</option>
                        <option value='What was your first pets name?'>What was your first pets name?</option>
                        <option value='In which city did your parents meet?'>In which city did your parents meet?</option>
                        <option value='What city were you born in?'>What city were you born in?</option>
                        <option value='What is your grandmothers maidin name?'>What is your grandmothers maidin name?</option>
                        <option value='What is the manufacturer of your first car?'>What is the manufacturer of your first car?</option>
                        <option value='What is your favorite country to visit?'>What is your favorite country to visit?</option>
                    </select>
                </fieldset>
                <fieldset>
                    <legend >Security Answer</legend>
                    <input type='text'
                        name='securityA'
                        id='register-securityA'
                        maxLength={35}
                        placeholder='Answer for your security question'
                        onChange={updateRegisterDataFunc}
                    />
                </fieldset>
                {/* Submit */}
                <div className='auth-submit'>
                    <ReCaptcha
                        id='auth-register-recaptcha'
                        sitekey="6Lc0b3MpAAAAAJsYiKn7HxyTlurydYOORTMJjn8V" 
                        theme='dark'
                        ref={refCaptcha}
                    />
                    <button className='auth-register-button'>Register</button>
                </div>
            </form>
        </div>
    )
}

